// src/api/api.ts
export const fetchExpenses = async (email: string) => {
  const response = await fetch(`/api/expenses?email=${email}`);
  return response.json();
};

export const fetchIncomes = async (email: string) => {
  const response = await fetch(`/api/incomes?email=${email}`);
  return response.json();
};

export const fetchCategories = async (email: string) => {
  const response = await fetch(`/api/categories?email=${email}`);
  return response.json();
};
