import React, { useState } from 'react';

interface Expense {
  ExpenseID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

const Expenses: React.FC<{ expenses: Expense[], updateExpense: (expense: Expense) => void, deleteExpense: (id: number) => void, createExpense: (newExpense: Omit<Expense, 'ExpenseID'>) => void }> = ({ expenses, updateExpense, deleteExpense, createExpense }) => {
  const [newExpense, setNewExpense] = useState<Omit<Expense, 'ExpenseID'>>({ description: '', amount: 0, date: '', categoryName: '' });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewExpense(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createExpense(newExpense);
    setNewExpense({ description: '', amount: 0, date: '', categoryName: '' });
  };

  return (
    <div>
      <h2>Expenses</h2>
      <ul>
        {expenses.map(expense => (
          <li key={expense.ExpenseID}>
            {expense.date}: {expense.description} - ${expense.amount} (Category: {expense.categoryName})
            <button onClick={() => updateExpense(expense)}>Update</button>
            <button onClick={() => deleteExpense(expense.ExpenseID)}>Delete</button>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <h3>Create Expense</h3>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={newExpense.description}
            onChange={handleChange}
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            name="amount"
            value={newExpense.amount}
            onChange={handleChange}
          />
        </label>
        <label>
          Date:
          <input
            type="date"
            name="date"
            value={newExpense.date}
            onChange={handleChange}
          />
        </label>
        <label>
          Category Name:
          <input
            type="text"
            name="categoryName"
            value={newExpense.categoryName}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default Expenses;
