import React, { useState } from 'react';

interface UploadExpensesProps {
  userEmail: string;  // הוספתי את ה-prop userEmail כאן
}

const UploadExpenses: React.FC<UploadExpensesProps> = ({ userEmail }) => {
  const [file, setFile] = useState<File | null>(null);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files ? e.target.files[0] : null;
    setFile(file);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`/api/expenses/upload-expenses`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${userEmail}`  // אם יש צורך לשלוח את ה-email כאן
        },
        body: formData
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error uploading file:', errorText);
        return;
      }

      console.log('File uploaded successfully');
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div>
      <h3>Upload Expenses from Excel</h3>
      <input type="file" accept=".xlsx, .xls, .csv" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={!file}>
        Upload
      </button>
    </div>
  );
};

export default UploadExpenses;
