import React, { useState } from 'react';

interface Expense {
  ExpenseID: number;
  Description: string;
  Amount: number;
  Date: string;
  CategoryName: string;
}

const Expenses: React.FC<{ expenses: Expense[], updateExpense: (expense: Expense) => void, deleteExpense: (id: number) => void, createExpense: (newExpense: Omit<Expense, 'ExpenseID'>) => void, email: string }> = ({ expenses, updateExpense, deleteExpense, createExpense, email }) => {
  const [newExpense, setNewExpense] = useState<Omit<Expense, 'ExpenseID'>>({ Description: '', Amount: 0, Date: '', CategoryName: '' });
  const [error, setError] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewExpense(prev => ({ ...prev, [name]: name === 'Amount' ? parseFloat(value) : value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Submitting new expense:", newExpense);

    if (!newExpense.Date) {
      setError("Date is required.");
      return;
    }

    if (isNaN(newExpense.Amount)) {
      setError("Amount must be a valid number.");
      return;
    }

    setError(null);
    createExpense({
      ...newExpense,
      Email: email,
    });
    setNewExpense({ Description: '', Amount: 0, Date: '', CategoryName: '' });
  };

  console.log("Rendering Expenses:", expenses);

  return (
    <div>
      <h2>Expenses</h2>
      <ul>
        {expenses.map(expense => (
          <li key={expense.ExpenseID}>
            {expense.Date}: {expense.Description} - ${expense.Amount} (Category: {expense.CategoryName})
            <button onClick={() => updateExpense(expense)}>Update</button>
            <button onClick={() => deleteExpense(expense.ExpenseID)}>Delete</button>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <h3>Create Expense</h3>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <label>
          Description:
          <input
            type="text"
            name="Description"
            value={newExpense.Description}
            onChange={handleChange}
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            name="Amount"
            value={newExpense.Amount}
            onChange={handleChange}
          />
        </label>
        <label>
          Date:
          <input
            type="date"
            name="Date"
            value={newExpense.Date}
            onChange={handleChange}
          />
        </label>
        <label>
          Category Name:
          <input
            type="text"
            name="CategoryName"
            value={newExpense.CategoryName}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default Expenses;
