import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Expense } from '../Transactions/TransactionsTypes';

interface UploadExpensesProps {
  userEmail: string;
  createExpense: (newExpense: Omit<Expense, 'ExpenseID'>) => void;
}

interface RowData {
  Description: string;
  Amount: number;
  Date: string;
  CategoryName: string;
}

const UploadExpenses: React.FC<UploadExpensesProps> = ({ userEmail, createExpense }) => {
  const [error, setError] = useState<string | null>(null);

  // Handle file upload
  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) {
      setError('No file selected.');
      return;
    }

    const reader = new FileReader();

    reader.onload = (event) => {
      const data = new Uint8Array(event.target?.result as ArrayBuffer);
      const workbook = XLSX.read(data, { type: 'array' });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(sheet);

      try {
        const expenses: Omit<Expense, 'ExpenseID'>[] = (jsonData as RowData[]).map((row) => {
          // Validate required fields
          if (!row.Description || !row.Amount || !row.Date || !row.CategoryName) {
            throw new Error('Missing required fields in Excel file.');
          }

          return {
            Description: row.Description,
            Amount: parseFloat(row.Amount.toString()),
            Date: new Date(row.Date).toISOString().split('T')[0], // Ensure "yyyy-MM-dd" format
            CategoryName: row.CategoryName,
            UserEmail: userEmail, // Use provided email
          };
        });

        // Upload each expense
        expenses.forEach(createExpense);
        setError(null); // Clear errors on success
      } catch (error) {
        setError('Error processing file. Please check the format.');
      }
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <div>
      <h3>Upload Expenses</h3>
      <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default UploadExpenses;
