import React, { useState, useEffect } from 'react';
import Expenses from './Expenses';
import Incomes from './Incomes';

interface Expense {
  ExpenseID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

interface Income {
  IncomeID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

const Transactions: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const email = "example@example.com";

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await fetch(`/api/expenses/${email}`);
        const data = await response.json();
        console.log("Fetched Expenses:", data);
        setExpenses(data);
      } catch (error) {
        console.error("Error fetching expenses:", error);
      }
    };

    const fetchIncomes = async () => {
      try {
        const response = await fetch(`/api/incomes/${email}`);
        const data = await response.json();
        console.log("Fetched Incomes:", data);
        setIncomes(data);
      } catch (error) {
        console.error("Error fetching incomes:", error);
      }
    };

    fetchExpenses();
    fetchIncomes();
  }, [email]);

  const updateExpense = async (expense: Expense) => {
    try {
      const response = await fetch(`/api/expenses/${expense.ExpenseID}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(expense),
      });
      const updatedExpense = await response.json();
      setExpenses(expenses.map(e => (e.ExpenseID === updatedExpense.ExpenseID ? updatedExpense : e)));
    } catch (error) {
      console.error("Error updating expense:", error);
    }
  };

  const deleteExpense = async (id: number) => {
    try {
      await fetch(`/api/expenses/${id}`, {
        method: 'DELETE',
      });
      setExpenses(expenses.filter(e => e.ExpenseID !== id));
    } catch (error) {
      console.error("Error deleting expense:", error);
    }
  };

  const createExpense = async (newExpense: Omit<Expense, 'ExpenseID'>) => {
    try {
      const response = await fetch('/api/expenses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newExpense),
      });
      const createdExpense = await response.json();
      setExpenses([...expenses, createdExpense]);
    } catch (error) {
      console.error("Error creating expense:", error);
    }
  };

  const updateIncome = async (income: Income) => {
    try {
      const response = await fetch(`/api/incomes/${income.IncomeID}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(income),
      });
      const updatedIncome = await response.json();
      setIncomes(incomes.map(i => (i.IncomeID === updatedIncome.IncomeID ? updatedIncome : i)));
    } catch (error) {
      console.error("Error updating income:", error);
    }
  };

  const deleteIncome = async (id: number) => {
    try {
      await fetch(`/api/incomes/${id}`, {
        method: 'DELETE',
      });
      setIncomes(incomes.filter(i => i.IncomeID !== id));
    } catch (error) {
      console.error("Error deleting income:", error);
    }
  };

  const createIncome = async (newIncome: Omit<Income, 'IncomeID'>) => {
    try {
      const response = await fetch('/api/incomes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newIncome),
      });
      const createdIncome = await response.json();
      setIncomes([...incomes, createdIncome]);
    } catch (error) {
      console.error("Error creating income:", error);
    }
  };

  return (
    <div>
      <h1>Transactions</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <Expenses
          expenses={expenses}
          updateExpense={updateExpense}
          deleteExpense={deleteExpense}
          createExpense={createExpense}
        />
        <Incomes
          incomes={incomes}
          updateIncome={updateIncome}
          deleteIncome={deleteIncome}
          createIncome={createIncome}
        />
      </div>
    </div>
  );
};

export default Transactions;
