import React, { useState } from 'react';
import { Income } from '../types'; // Importing the Income interface

interface IncomeFormProps {
  setIncomes: React.Dispatch<React.SetStateAction<Income[]>>;
  categories: string[];
}

const IncomeForm: React.FC<IncomeFormProps> = ({ setIncomes, categories }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState(0);
  const [date, setDate] = useState('');
  const [category, setCategory] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    // Function to create a new income on the server
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <input value={amount} onChange={(e) => setAmount(+e.target.value)} placeholder="Amount" />
      <input value={date} onChange={(e) => setDate(e.target.value)} placeholder="Date" />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        {categories.map(category => (
          <option key={category} value={category}>{category}</option>
        ))}
      </select>
      <button type="submit">Add Income</button>
    </form>
  );
};

export default IncomeForm;
