import React, { useEffect, useState } from 'react';
import './Analytics.css';
import ExpensesSummary from './ExpensesSummary';
import IncomesSummary from './IncomesSummary';
import ExpensesByCategoryChart from './ExpensesByCategoryChart';
import ExpensesByMonthChart from './ExpensesByMonthChart';
import AnalyticsMetrics from './AnalyticsMetrics';

interface Expense {
  ExpenseID: number;
  CategoryID: number;
  CategoryName: string;
  Amount: number;
  Date: Date;
  Description?: string;
}

interface Income {
  IncomeID: number;
  Amount: number;
  Date: Date;
  Description?: string;
}

interface Category {
  CategoryID: number;
  CategoryName: string;
  Email: string;
}

const Analytics: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const [categories, setCategories] = useState<Category[]>([]);
  const [userEmail, setUserEmail] = useState<string>('');

  const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUserEmail(event.target.value);
  };

  useEffect(() => {
    if (userEmail) {
      fetch(`/api/expenses/${userEmail}`)
        .then(response => response.json())
        .then((data: Expense[]) => setExpenses(data))
        .catch(error => console.error('Error fetching expenses:', error));

      fetch(`/api/incomes/${userEmail}`)
        .then(response => response.json())
        .then((data: Income[]) => setIncomes(data))
        .catch(error => console.error('Error fetching incomes:', error));

      fetch(`/api/categories/`)
        .then(response => response.json())
        .then((data: Category[]) => setCategories(data))
        .catch(error => console.error('Error fetching categories:', error));
    }
  }, [userEmail]);

  const expensesByCategory = expenses.reduce<Record<string, number>>((acc, expense) => {
    const categoryName = expense.CategoryName || `Category ${expense.CategoryID}`;
    acc[categoryName] = (acc[categoryName] || 0) + expense.Amount;
    return acc;
  }, {});

  const expensesByCategoryArray = Object.entries(expensesByCategory).map(([CategoryName, Amount]) => ({
    CategoryName,
    Amount,
  }));

  return (
    <div className="analytics-container">
      <h1>Analytics</h1>
      <label htmlFor="emailInput">Enter your email:</label>
      <input
        type="email"
        id="emailInput"
        value={userEmail}
        onChange={handleEmailChange}
        placeholder="example@example.com"
      />

      {/* Summary of Expenses and Incomes */}
      <div className="summary-container">
        <div className="summary">
          <h2>Expenses Summary</h2>
          <ExpensesSummary expenses={expenses} />
        </div>
        <div className="summary">
          <h2>Incomes Summary</h2>
          <IncomesSummary incomes={incomes} />
        </div>
      </div>

      {/* Charts */}
      <div className="charts-container">
        <div className="chart">
          <ExpensesByCategoryChart expensesByCategory={expensesByCategoryArray} />
        </div>
        <div className="chart">
          <ExpensesByMonthChart monthlyExpenses={expenses} />
        </div>
      </div>
    </div>
  );
};

export default Analytics;
