import React, { useState, useEffect, useRef, useCallback } from 'react';

// Define interfaces for Expense and Income objects
interface Expense {
  id: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

interface Income {
  id: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

const Transactions: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const email = "example@example.com"; 
  const expensesRef = useRef<Expense[]>([]);
  const incomesRef = useRef<Income[]>([]);

  // Fetch expenses and incomes from the server
  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/expenses/${email}`);
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }
        const text = await response.text();
        try {
          const data = JSON.parse(text);
          const mappedData = data.map((expense: any) => ({
            id: expense.ExpenseID,
            description: expense.Description,
            amount: expense.Amount,
            date: expense.Date,
            categoryName: expense.CategoryName
          }));
          expensesRef.current = mappedData;
          setExpenses(mappedData);
        } catch (jsonError) {
          if (jsonError instanceof Error) {
            throw new Error('Failed to parse JSON: ' + jsonError.message);
          } else {
            throw new Error('Failed to parse JSON');
          }
        }
      } catch (error) {
        console.error("Error fetching expenses:", error);
      }
    };

    const fetchIncomes = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/incomes/${email}`);
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }
        const text = await response.text();
        try {
          const data = JSON.parse(text);
          const mappedData = data.map((income: any) => ({
            id: income.IncomeID,
            description: income.Description,
            amount: income.Amount,
            date: income.Date,
            categoryName: income.CategoryName
          }));
          incomesRef.current = mappedData;
          setIncomes(mappedData);
        } catch (jsonError) {
          if (jsonError instanceof Error) {
            throw new Error('Failed to parse JSON: ' + jsonError.message);
          } else {
            throw new Error('Failed to parse JSON');
          }
        }
      } catch (error) {
        console.error("Error fetching incomes:", error);
      }
    };

    fetchExpenses();
    fetchIncomes();
  }, [email]);

  // Function to create a new expense
  const createExpense = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:3000/api/expenses`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          Email: email,
          Amount: 100,
          Date: new Date().toISOString(),
          CategoryName: 'Food',
          Description: 'Groceries'
        })
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const newExpense = await response.json();
      setExpenses((prevExpenses) => [...prevExpenses, newExpense]);
    } catch (error) {
      console.error("Error creating expense:", error);
    }
  }, [email]);

  // Function to update an existing expense
  const updateExpense = useCallback(async (id: number) => {
    try {
      const response = await fetch(`http://localhost:3000/api/expenses/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          Email: email,
          Amount: 150,
          Date: new Date().toISOString(),
          CategoryName: 'Food',
          Description: 'Groceries and snacks'
        })
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const updatedExpense = await response.json();
      setExpenses((prevExpenses) => prevExpenses.map(expense => expense.id === id ? updatedExpense : expense));
    } catch (error) {
      console.error("Error updating expense:", error);
    }
  }, [email]);

  // Function to delete an expense
  const deleteExpense = useCallback(async (id: number) => {
    try {
      const response = await fetch(`http://localhost:3000/api/expenses/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      setExpenses((prevExpenses) => prevExpenses.filter(expense => expense.id !== id));
    } catch (error) {
      console.error("Error deleting expense:", error);
    }
  }, []);

  // Function to create a new income
  const createIncome = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:3000/api/incomes`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          Email: email,
          Amount: 2000,
          Date: new Date().toISOString(),
          CategoryName: 'Salary',
          Description: 'Monthly salary'
        })
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const newIncome = await response.json();
      setIncomes((prevIncomes) => [...prevIncomes, newIncome]);
    } catch (error) {
      console.error("Error creating income:", error);
    }
  }, [email]);

  // Function to update an existing income
  const updateIncome = useCallback(async (id: number) => {
    try {
      const response = await fetch(`http://localhost:3000/api/incomes/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          Email: email,
          Amount: 2500,
          Date: new Date().toISOString(),
          CategoryName: 'Salary',
          Description: 'Monthly salary with bonus'
        })
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const updatedIncome = await response.json();
      setIncomes((prevIncomes) => prevIncomes.map(income => income.id === id ? updatedIncome : income));
    } catch (error) {
      console.error("Error updating income:", error);
    }
  }, [email]);

  // Function to delete an income
  const deleteIncome = useCallback(async (id: number) => {
    try {
      const response = await fetch(`http://localhost:3000/api/incomes/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      setIncomes((prevIncomes) => prevIncomes.filter(income => income.id !== id));
    } catch (error) {
      console.error("Error deleting income:", error);
    }
  }, []);

  return (
    <div>
      <h1>Transactions</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <Expenses expenses={expenses} updateExpense={updateExpense} deleteExpense={deleteExpense} createExpense={createExpense} />
        <Incomes incomes={incomes} updateIncome={updateIncome} deleteIncome={deleteIncome} createIncome={createIncome} />
      </div>
    </div>
  );
};

// Expenses component
const Expenses: React.FC<{ expenses: Expense[], updateExpense: (id: number) => void, deleteExpense: (id: number) => void, createExpense: () => void }> = ({ expenses, updateExpense, deleteExpense, createExpense }) => (
  <div>
    <h2>Expenses</h2>
    <ul>
      {expenses.map(expense => (
        <li key={expense.id}>
          {expense.date}: {expense.description} - ${expense.amount} (Category: {expense.categoryName})
          <button onClick={() => updateExpense(expense.id)}>Update</button>
          <button onClick={() => deleteExpense(expense.id)}>Delete</button>
        </li>
      ))}
    </ul>
    <button onClick={createExpense}>Create Expense</button>
  </div>
);

// Incomes component
const Incomes: React.FC<{ incomes: Income[], updateIncome: (id: number) => void, deleteIncome: (id: number) => void, createIncome: () => void }> = ({ incomes, updateIncome, deleteIncome, createIncome }) => (
  <div>
    <h2>Incomes</h2>
    <ul>
      {incomes.map(income => (
        <li key={income.id}>
          {income.date}: {income.description} - ${income.amount} (Category: {income.categoryName})
          <button onClick={() => updateIncome(income.id)}>Update</button>
          <button onClick={() => deleteIncome(income.id)}>Delete</button>
        </li>
      ))}
    </ul>
    <button onClick={createIncome}>Create Income</button>
  </div>
);

export default Transactions;
