import React, { useState } from 'react';
import { Expense, Category } from '../types'; // Importing the interfaces

interface ExpenseFormProps {
  setExpenses: React.Dispatch<React.SetStateAction<Expense[]>>;
  categories: Category[];
}

const ExpenseForm: React.FC<ExpenseFormProps> = ({ setExpenses, categories }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState(0);
  const [date, setDate] = useState('');
  const [category, setCategory] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    // Function to create a new expense on the server
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <input value={amount} onChange={(e) => setAmount(+e.target.value)} placeholder="Amount" />
      <input value={date} onChange={(e) => setDate(e.target.value)} placeholder="Date" />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        {categories.map(category => (
          <option key={category.CategoryID} value={category.CategoryName}>
            {category.CategoryName}
          </option>
        ))}
      </select>
      <button type="submit">Add Expense</button>
    </form>
  );
};

export default ExpenseForm;
