import React, { useState, useEffect } from 'react';
import '../styles/styles.css'; // Importing the CSS file
import Expenses from './Expenses';
import Incomes from './Incomes';

interface Expense {
  ExpenseID: number;
  Description: string;
  Amount: number;
  Date: string;
  CategoryName: string;
}

interface Income {
  IncomeID: number;
  Description: string;
  Amount: number;
  Date: string;
  CategoryName: string;
}

const Transactions: React.FC = () => {
  const [email, setEmail] = useState('example@example.com');
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const [categories, setCategories] = useState<string[]>([]);

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await fetch(`/api/expenses?email=${email}`);
        const data = await response.json();
        setExpenses(data);
      } catch (error) {
        console.error('Error fetching expenses:', error);
      }
    };

    const fetchIncomes = async () => {
      try {
        const response = await fetch(`/api/incomes?email=${email}`);
        const data = await response.json();
        setIncomes(data);
      } catch (error) {
        console.error('Error fetching incomes:', error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await fetch(`/api/categories?email=${email}`);
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchExpenses();
    fetchIncomes();
    fetchCategories();
  }, [email]);

  const updateExpense = (updatedExpense: Expense) => {
    setExpenses(prevExpenses => prevExpenses.map(exp => exp.ExpenseID === updatedExpense.ExpenseID ? updatedExpense : exp));
  };

  const deleteExpense = (id: number) => {
    setExpenses(prevExpenses => prevExpenses.filter(exp => exp.ExpenseID !== id));
  };

  const createExpense = (newExpense: Omit<Expense, 'ExpenseID'>) => {
    const newExpenseWithID: Expense = {
      ...newExpense,
      ExpenseID: expenses.length > 0 ? Math.max(...expenses.map(exp => exp.ExpenseID)) + 1 : 1,
    };
    setExpenses([...expenses, newExpenseWithID]);
  };

  const updateIncome = (updatedIncome: Income) => {
    setIncomes(prevIncomes => prevIncomes.map(inc => inc.IncomeID === updatedIncome.IncomeID ? updatedIncome : inc));
  };

  const deleteIncome = (id: number) => {
    setIncomes(prevIncomes => prevIncomes.filter(inc => inc.IncomeID !== id));
  };

  const createIncome = (newIncome: Omit<Income, 'IncomeID'>) => {
    const newIncomeWithID: Income = {
      ...newIncome,
      IncomeID: incomes.length > 0 ? Math.max(...incomes.map(inc => inc.IncomeID)) + 1 : 1,
    };
    setIncomes([...incomes, newIncomeWithID]);
  };

  const addCategory = (category: string) => {
    setCategories([...categories, category]);
  };

  return (
    <div>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Enter your email"
      />
      <Expenses
        expenses={expenses}
        updateExpense={updateExpense}
        deleteExpense={deleteExpense}
        createExpense={createExpense}
        addCategory={addCategory}
      />
      <Incomes
        incomes={incomes}
        updateIncome={updateIncome}
        deleteIncome={deleteIncome}
        createIncome={createIncome}
        addCategory={addCategory}
      />
    </div>
  );
};

export default Transactions;
