import React from 'react';
import {
  Chart as ChartJS,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const ExpensesByCategoryChart: React.FC<{ expensesByCategory: Array<any> }> = ({ expensesByCategory }) => {
  console.log("Expenses by Category data:", expensesByCategory);

  const data = {
    labels: expensesByCategory.map(expense => expense.CategoryName),
    datasets: [{
      data: expensesByCategory.map(expense => expense.Amount),
      backgroundColor: [
        '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40',
      ], 
    }],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allows resizing without maintaining aspect ratio
    plugins: {
      legend: {
        display: true, // Ensures the legend is displayed
        position: 'top', // Sets legend position
      },
    },
    layout: {
      padding: {
        left: 10,
        right: 10,
        top: 10,
        bottom: 10,
      },
    },
  };

  return (
    <div className="expenses-by-category-chart" style={{ width: '300px', height: '300px' }}>
      <h2>Expenses by Category</h2>
      <Pie data={data} options={options} />
    </div>
  );
};

export default ExpensesByCategoryChart;
