import React, { useState, useEffect } from 'react';
import CategorySelector from './CategorySelector';
import BudgetInput from './BudgetInput';

const MonthlyBudget = ({ userEmail }) => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [monthlyBudgets, setMonthlyBudgets] = useState([]);

  useEffect(() => {
    // Fetch categories from the backend
    fetch(`/api/categories?email=${userEmail}`)
      .then(response => response.json())
      .then(data => setCategories(data))
      .catch(error => console.error('Error fetching categories:', error));
  }, [userEmail]);

  useEffect(() => {
    // Fetch monthly budgets from the backend
    fetch(`/api/monthlyBudgets/${userEmail}`)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched budgets:', data); // Log the fetched data
        if (Array.isArray(data)) {
          setMonthlyBudgets(data);
        } else {
          console.error('Error: Data is not an array', data);
        }
      })
      .catch(error => console.error('Error fetching budgets:', error));
  }, [userEmail]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleBudgetSave = (amount) => {
    // Save budget to the backend
    const data = {
      Email: userEmail,
      CategoryName: selectedCategory,
      BudgetAmount: parseFloat(amount),
      SpentAmount: 0, // Initial value
      BudgetMonth: new Date().toISOString()
    };

    console.log('Data sent:', data); // Log the data being sent

    fetch('/api/monthlyBudgets', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then(response => {
        console.log('Response status:', response.status); // Log the response status
        if (!response.ok) {
          return response.json().then(error => { throw new Error(error.error) });
        }
        return response.json();
      })
      .then(data => {
        alert('Budget saved successfully!');
        if (Array.isArray(monthlyBudgets)) {
          setMonthlyBudgets([...monthlyBudgets, data]);
        } else {
          console.error('Error: monthlyBudgets is not an array', monthlyBudgets);
        }
      })
      .catch(error => console.error('Error saving budget:', error));
  };

  return (
    <div>
      <h2>Monthly Budget</h2>
      <CategorySelector categories={categories} onSelect={handleCategorySelect} />
      {selectedCategory && <BudgetInput onSave={handleBudgetSave} />}

      <h3>Your Monthly Budgets</h3>
      <ul>
        {Array.isArray(monthlyBudgets) && monthlyBudgets.map((budget, index) => (
          <li key={index}>
            {new Date(budget.BudgetMonth).toLocaleDateString()} - {budget.CategoryName}: ${budget.BudgetAmount} (Spent: ${budget.SpentAmount})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MonthlyBudget;
