import React, { useState, useEffect } from 'react';
import Expenses from './components/Expenses';
import Incomes from './components/Incomes';

interface Expense {
  ExpenseID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

interface Income {
  IncomeID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

const Transactions: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const email = "example@example.com";

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await fetch(`/api/expenses/${email}`);
        console.log("Expenses response:", response);
        if (!response.ok) {
          console.error("Failed to fetch expenses");
          return;
        }
        const data = await response.json();
        console.log("Expenses data:", data);
        setExpenses(data);
      } catch (error) {
        console.error("Error fetching expenses:", error);
      }
    };

    const fetchIncomes = async () => {
      try {
        const response = await fetch(`/api/incomes/${email}`);
        console.log("Incomes response:", response);
        if (!response.ok) {
          console.error("Failed to fetch incomes");
          return;
        }
        const data = await response.json();
        console.log("Incomes data:", data);
        setIncomes(data);
      } catch (error) {
        console.error("Error fetching incomes:", error);
      }
    };

    fetchExpenses();
    fetchIncomes();
  }, [email]);

  return (
    <div>
      <h1>Transactions</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <Expenses
          expenses={expenses}
          updateExpense={(expense) => console.log('Updating expense:', expense)}
          deleteExpense={(id) => console.log('Deleting expense:', id)}
          createExpense={(newExpense) => console.log('Creating expense:', newExpense)}
        />
        <Incomes
          incomes={incomes}
          updateIncome={(income) => console.log('Updating income:', income)}
          deleteIncome={(id) => console.log('Deleting income:', id)}
          createIncome={(newIncome) => console.log('Creating income:', newIncome)}
        />
      </div>
    </div>
  );
};

export default Transactions;
