import React, { useEffect, useState } from 'react';
import ExpensesSummary from './ExpensesSummary';
import IncomesSummary from './IncomesSummary';
import ExpensesByCategoryChart from './ExpensesByCategoryChart';
import ExpensesByMonthChart from './ExpensesByMonthChart';
import AnalyticsMetrics from './AnalyticsMetrics';

interface Expense {
  ExpenseID: number;
  CategoryID: number;  // ID של הקטגוריה
  Amount: number;
  Date: Date;
  Description?: string;
}

interface Income {
  IncomeID: number;
  Amount: number;
  Date: Date;
  Description?: string;
}

const Analytics: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const [analytics, setAnalytics] = useState<any[]>([]);
  const [userEmail, setUserEmail] = useState<string>('');

  const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUserEmail(event.target.value);
  };

  useEffect(() => {
    if (userEmail) {
      console.log(`Fetching data for email: ${userEmail}`);

      // Fetch expenses data
      fetch(`/api/expenses/${userEmail}`)
        .then(response => response.json())
        .then((data: Expense[]) => {
          console.log("Expenses data:", data); // לוג נתוני ההוצאות
          setExpenses(data);
        })
        .catch(error => {
          console.error('Error fetching expenses:', error);
          alert('Failed to fetch expenses');
        });

      // Fetch incomes data
      fetch(`/api/incomes/${userEmail}`)
        .then(response => response.json())
        .then((data: Income[]) => {
          console.log("Incomes data:", data); // לוג נתוני ההכנסות
          setIncomes(data);
        })
        .catch(error => {
          console.error('Error fetching incomes:', error);
          alert('Failed to fetch incomes');
        });

      // Fetch analytics data
      fetch(`/api/analytics/${userEmail}`)
        .then(response => response.json())
        .then(data => {
          console.log("Analytics data:", data); // לוג נתוני האנליטיקס
          setAnalytics(data);
        })
        .catch(error => {
          console.error('Error fetching analytics:', error);
          alert('Failed to fetch analytics');
        });
    }
  }, [userEmail]);

  const monthlyExpenses = analytics.filter(item => item.Metric === 'monthly_expenses');

  // Group expenses by category for the pie chart
  const expensesByCategory = expenses.reduce<Record<string, number>>((acc, expense) => {
    const categoryName = `Category ${expense.CategoryID}`; // תוכל לשדרג עם שם הקטגוריה או להישאר עם ה-ID
    if (!acc[categoryName]) {
      acc[categoryName] = 0;
    }
    acc[categoryName] += expense.Amount;
    return acc;
  }, {});

  const expensesByCategoryArray = Object.entries(expensesByCategory).map(([CategoryName, Amount]) => ({ CategoryName, Amount }));

  console.log("Expenses by Category Array:", expensesByCategoryArray); // לוג הקטגוריות וההוצאות

  return (
    <div className="analytics">
      <h1>Analytics</h1>
      <label htmlFor="emailInput">Enter your email:</label>
      <input
        type="email"
        id="emailInput"
        value={userEmail}
        onChange={handleEmailChange}
        placeholder="example@example.com"
      />
      <ExpensesSummary expenses={expenses} />
      <IncomesSummary incomes={incomes} />
      <ExpensesByCategoryChart expensesByCategory={expensesByCategoryArray} />
      <ExpensesByMonthChart monthlyExpenses={monthlyExpenses} />
      <AnalyticsMetrics analytics={analytics} />
    </div>
  );
};

export default Analytics;
