import React, { useState, useEffect } from 'react';
import CategorySelector from './CategorySelector';
import BudgetInput from './BudgetInput';

const MonthlyBudget = () => {
  const [userEmail, setUserEmail] = useState('');
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [monthlyBudgets, setMonthlyBudgets] = useState([]);
  const [expenses, setExpenses] = useState([]); // Add state for expenses
  const [showBudgetForm, setShowBudgetForm] = useState(false); // Add state to show/hide the budget form

  useEffect(() => {
    if (userEmail) {
      // Fetch expense categories from the expenses table
      fetch(`/api/expenses/${userEmail}`)
        .then(response => response.json())
        .then(data => {
          console.log('Fetched expense categories:', data); // Log the fetched expense categories
          const categoryNames = [...new Set(data.map(expense => expense.CategoryName.toLowerCase()))]; // Extract unique category names in lowercase
          console.log('Expense categories:', categoryNames); // Log the expense categories
          setCategories(categoryNames);
          setExpenses(data); // Save expenses to state
        })
        .catch(error => console.error('Error fetching expense categories:', error));

      // Fetch monthly budgets from the backend
      fetch(`/api/monthlyBudgets/${userEmail}`)
        .then(response => response.json())
        .then(data => {
          console.log('Fetched budgets:', data); // Log the fetched data
          if (Array.isArray(data)) {
            setMonthlyBudgets(data);
          } else {
            console.error('Error: Data is not an array', data);
          }
        })
        .catch(error => console.error('Error fetching budgets:', error));
    }
  }, [userEmail]);

  const handleCategorySelect = (category) => {
    console.log('Selected category:', category); // Log the selected category
    setSelectedCategory(category.toLowerCase()); // Ensure selected category is in lowercase
  };

  const handleBudgetSave = (amount) => {
    // Check if the category already exists in the budgets
    const categoryExists = monthlyBudgets.some(budget => budget.CategoryName.toLowerCase() === selectedCategory);
    if (categoryExists) {
      alert('Category already exists in your budgets.');
      return;
    }

    // Save budget to the backend
    const data = {
      Email: userEmail,
      CategoryName: selectedCategory,
      BudgetAmount: parseFloat(amount),
      SpentAmount: 0, // Initial value
      BudgetMonth: new Date().toISOString()
    };

    console.log('Data sent:', data); // Log the data being sent

    fetch('/api/monthlyBudgets', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then(response => {
        console.log('Response status:', response.status); // Log the response status
        if (!response.ok) {
          return response.json().then(error => { throw new Error(error.error) });
        }
        return response.json();
      })
      .then(data => {
        alert('Budget saved successfully!');
        if (Array.isArray(monthlyBudgets)) {
          setMonthlyBudgets([...monthlyBudgets, data]);
        } else {
          console.error('Error: monthlyBudgets is not an array', monthlyBudgets);
        }
        setShowBudgetForm(false); // Hide the budget form after saving
      })
      .catch(error => console.error('Error saving budget:', error));
  };

  const handleBudgetDelete = (budgetId) => {
    fetch(`/api/monthlyBudgets/${budgetId}`, {
      method: 'DELETE',
    })
      .then(response => {
        if (response.ok) {
          setMonthlyBudgets(monthlyBudgets.filter(budget => budget.BudgetID !== budgetId));
        } else {
          console.error('Error deleting budget');
        }
      })
      .catch(error => console.error('Error deleting budget:', error));
  };

  const calculateSpentAmount = (categoryName) => {
    const lowerCategoryName = categoryName.toLowerCase();
    return expenses
      .filter(expense => expense.CategoryName.toLowerCase() === lowerCategoryName)
      .reduce((total, expense) => total + expense.Amount, 0); // Sum the Amount from expenses
  };

  const isOverBudget = (budget) => {
    const spentAmount = calculateSpentAmount(budget.CategoryName);
    return spentAmount > budget.BudgetAmount;
  };

  return (
    <div>
      <h2>Monthly Budget</h2>
      <input 
        type="email" 
        placeholder="Enter your email"
        value={userEmail}
        onChange={(e) => setUserEmail(e.target.value)} 
      />
      {userEmail && (
        <>
          <button onClick={() => setShowBudgetForm(true)}>Create Monthly Budget</button> {/* Button to show the budget form */}

          {showBudgetForm && (
            <div>
              <CategorySelector 
                categories={["Select Category", ...categories]} // Add "Select Category" to the dropdown
                onSelect={handleCategorySelect} 
              />
              {selectedCategory && selectedCategory !== "Select Category" && (
                <BudgetInput onSave={handleBudgetSave} />
              )}
            </div>
          )}

          <h3>Your Monthly Budgets</h3>
          <ul>
            {Array.isArray(monthlyBudgets) && monthlyBudgets.map((budget, index) => (
              <li key={index}>
                {new Date(budget.BudgetMonth).toLocaleDateString()} - {budget.CategoryName}: ₪{budget.BudgetAmount} (Spent: ₪{calculateSpentAmount(budget.CategoryName)})
                <button onClick={() => handleBudgetDelete(budget.BudgetID)}>Delete</button> {/* Add delete button */}
                {isOverBudget(budget) && (
                  <p style={{ color: 'red' }}>Over budget!</p> // Show over budget warning in red
                )}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default MonthlyBudget;
