import React, { useState, useEffect } from 'react';
import CategorySelector from './CategorySelector';
import BudgetInput from './BudgetInput';

const MonthlyBudget = () => {
  const [userEmail, setUserEmail] = useState('');
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [monthlyBudgets, setMonthlyBudgets] = useState([]);

  useEffect(() => {
    if (userEmail) {
      // Fetch categories from the backend
      fetch(`/api/categories?email=${userEmail}`)
        .then(response => response.json())
        .then(data => {
          const categoryNames = data.map(category => category.CategoryName); // Extract category names
          setCategories(categoryNames);
        })
        .catch(error => console.error('Error fetching categories:', error));

      // Fetch monthly budgets from the backend
      fetch(`/api/monthlyBudgets/${userEmail}`)
        .then(response => response.json())
        .then(data => {
          console.log('Fetched budgets:', data); // Log the fetched data
          if (Array.isArray(data)) {
            setMonthlyBudgets(data);
          } else {
            console.error('Error: Data is not an array', data);
          }
        })
        .catch(error => console.error('Error fetching budgets:', error));
    }
  }, [userEmail]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleBudgetSave = (amount) => {
    // Check if the category already exists in the budgets
    const categoryExists = monthlyBudgets.some(budget => budget.CategoryName === selectedCategory);
    if (categoryExists) {
      alert('Category already exists in your budgets.');
      return;
    }

    // Save budget to the backend
    const data = {
      Email: userEmail,
      CategoryName: selectedCategory,
      BudgetAmount: parseFloat(amount),
      SpentAmount: 0, // Initial value
      BudgetMonth: new Date().toISOString()
    };

    console.log('Data sent:', data); // Log the data being sent

    fetch('/api/monthlyBudgets', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then(response => {
        console.log('Response status:', response.status); // Log the response status
        if (!response.ok) {
          return response.json().then(error => { throw new Error(error.error) });
        }
        return response.json();
      })
      .then(data => {
        alert('Budget saved successfully!');
        if (Array.isArray(monthlyBudgets)) {
          setMonthlyBudgets([...monthlyBudgets, data]);
        } else {
          console.error('Error: monthlyBudgets is not an array', monthlyBudgets);
        }
      })
      .catch(error => console.error('Error saving budget:', error));
  };

  const calculateSpentAmount = (categoryName) => {
    return monthlyBudgets
      .filter(budget => budget.CategoryName === categoryName)
      .reduce((total, budget) => total + budget.BudgetAmount, 0);
  };

  return (
    <div>
      <h2>Monthly Budget</h2>
      <input 
        type="email" 
        placeholder="Enter your email"
        value={userEmail}
        onChange={(e) => setUserEmail(e.target.value)} 
      />
      {userEmail && (
        <>
          <CategorySelector 
            categories={["Select Category", ...categories]} // Add "Select Category" to the dropdown
            onSelect={handleCategorySelect} 
          />
          {selectedCategory && selectedCategory !== "Select Category" && (
            <BudgetInput onSave={handleBudgetSave} />
          )}

          <h3>Your Monthly Budgets</h3>
          <ul>
            {Array.isArray(monthlyBudgets) && monthlyBudgets.map((budget, index) => (
              <li key={index}>
                {new Date(budget.BudgetMonth).toLocaleDateString()} - {budget.CategoryName}: ₪{budget.BudgetAmount} (Spent: ₪{calculateSpentAmount(budget.CategoryName)})
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default MonthlyBudget;
