import React, { useState } from 'react';

interface Income {
  IncomeID: number;
  description: string;
  amount: number;
  date: string;
  categoryName: string;
}

const Incomes: React.FC<{ incomes: Income[], updateIncome: (income: Income) => void, deleteIncome: (id: number) => void, createIncome: (newIncome: Omit<Income, 'IncomeID'>) => void }> = ({ incomes, updateIncome, deleteIncome, createIncome }) => {
  const [newIncome, setNewIncome] = useState<Omit<Income, 'IncomeID'>>({ description: '', amount: 0, date: '', categoryName: '' });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewIncome(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Submitting new income:", newIncome);
    createIncome(newIncome);
    setNewIncome({ description: '', amount: 0, date: '', categoryName: '' });
  };

  console.log("Rendering Incomes:", incomes);

  return (
    <div>
      <h2>Incomes</h2>
      <ul>
        {incomes.map(income => (
          <li key={income.IncomeID}>
            {income.date}: {income.description} - ${income.amount} (Category: {income.categoryName})
            <button onClick={() => updateIncome(income)}>Update</button>
            <button onClick={() => deleteIncome(income.IncomeID)}>Delete</button>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <h3>Create Income</h3>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={newIncome.description}
            onChange={handleChange}
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            name="amount"
            value={newIncome.amount}
            onChange={handleChange}
          />
        </label>
        <label>
          Date:
          <input
            type="date"
            name="date"
            value={newIncome.date}
            onChange={handleChange}
          />
        </label>
        <label>
          Category Name:
          <input
            type="text"
            name="categoryName"
            value={newIncome.categoryName}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default Incomes;
