import React, { useState, useEffect } from 'react';
import CategorySelector from './CategorySelector';
import BudgetInput from './BudgetInput';

const MonthlyBudget = ({ userEmail }) => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    // Fetch categories from the backend
    fetch(`/api/categories?email=${userEmail}`)
      .then(response => response.json())
      .then(data => {
        // Assuming data is an array of objects with {CategoryName, CategoryID, Email}
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  }, [userEmail]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleBudgetSave = (amount) => {
    // Save budget to the backend
    const data = {
      email: userEmail,
      categoryName: selectedCategory,
      budgetAmount: parseFloat(amount),
      spentAmount: 0, // Initial value
      budgetMonth: new Date()
    };

    fetch('/api/budgets', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then(response => response.json())
      .then(data => {
        alert('Budget saved successfully!');
      })
      .catch(error => console.error('Error saving budget:', error));
  };

  return (
    <div>
      <h2>Monthly Budget</h2>
      <CategorySelector categories={categories} onSelect={handleCategorySelect} />
      {selectedCategory && <BudgetInput onSave={handleBudgetSave} />}
    </div>
  );
};

export default MonthlyBudget;
