import React, { useState, useEffect } from 'react';
import { Expense, Income, Category } from '../types'; // Importing the interfaces
import { fetchExpenses, fetchIncomes, fetchCategories } from '../api/api'; // Correct import path
import Expenses from './Expenses';
import Incomes from './Incomes';
import Categories from './Categories';

const Transactions: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [incomes, setIncomes] = useState<Income[]>([]);
  const [categories, setCategories] = useState<Category[]>([]); // Adjusting type

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [expensesData, incomesData, categoriesData] = await Promise.all([
          fetchExpenses(),
          fetchIncomes(),
          fetchCategories(),
        ]);
        setExpenses(expensesData);
        setIncomes(incomesData);
        setCategories(categoriesData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <Categories
        categories={categories}
        setCategories={setCategories} // Passing the setCategories function to update the state
      />
      <Expenses
        expenses={expenses}
        setExpenses={setExpenses} // Passing the setExpenses function to update the state
        categories={categories} // Passing categories
      />
      <Incomes
        incomes={incomes}
        setIncomes={setIncomes} // Passing the setIncomes function to update the state
        categories={categories} // Passing categories
      />
    </div>
  );
};

export default Transactions;
